ValidationRule.errorConditionFormula:
AND(
    /* --- Check 1: Is the status being changed TO "Hired"? --- */
    ISPICKVAL(Status__c, "Hired"),
    ISCHANGED(Status__c),

    /* --- Check 2: Does the candidate's role require PHI access? --- */
    OR(
        ISPICKVAL(Role__c, "Nurse"),
        ISPICKVAL(Role__c, "Physician"),
        ISPICKVAL(Role__c, "Medical Assistant"),
        ISPICKVAL(Role__c, "Billing Staff")
    ),

    /* --- Check 3: Is the PHI Access flag FALSE? --- */
    PHI_Access_Granted__c = FALSE
)
